import React, { useEffect, useState } from 'react';

export default function CurrentLocation() {
    const [location, setLocation] = useState(null);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [fahrenheit, setFahrenheit] = useState(null);
    const [temperatureUnit, setTemperatureUnit] = useState('C'); // 'C' for Celsius, 'F' for Fahrenheit
    const [alerts, setAlerts] = useState(null); // Store weather alerts

    useEffect(() => {
        async function fetchCurrentLocation() {
            if (navigator.geolocation.getCurrentPosition) {
                navigator.geolocation.getCurrentPosition(async (position) => {
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;
                    const appid = "6a4306a0f2c73c28dc5c3bbf739e87d0";

                    try {
                        // Fetch both current weather and alerts using the One Call API
                        const response = await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=${appid}&units=metric`);
                        const data = await response.json();
                        
                        // Check if the location data was found
                        if (data.current) {
                            setLocation(data);
                            setIsLoading(false);

                            // Save weather data to localStorage
                            saveToLocalStorage(data);

                            // Calculate and set temperature in Fahrenheit
                            calculate(data);

                            // Check for weather alerts
                            if (data.alerts && data.alerts.length > 0) {
                                setAlerts(data.alerts);
                                showNotification(data.alerts[0].event, data.alerts[0].description);
                            }
                        } else {
                            setError('Location not found. Please ensure your location services are enabled.');
                            setIsLoading(false);
                        }
                    } catch (error) {
                        setError('Error fetching the location data. Please check your connection.');
                        setIsLoading(false);
                        console.error('Error fetching the location data:', error);
                    }
                }, (error) => {
                    handleGeolocationError(error);
                    loadFromLocalStorage(); // Fallback to cached data if available
                });
            } else {
                setError('Geolocation is not supported by this browser.');
                loadFromLocalStorage();
            }
        }

        fetchCurrentLocation();
    }, []);

    function handleGeolocationError(error) {
        switch (error.code) {
            case error.PERMISSION_DENIED:
                setError("Location access denied. Please allow location permissions in your browser.");
                break;
            case error.POSITION_UNAVAILABLE:
                setError("Location information is unavailable.");
                break;
            case error.TIMEOUT:
                setError("Request to get location timed out.");
                break;
            default:
                setError("An unknown error occurred.");
                break;
        }
    }

    function calculate(location) {
        const tempCelsius = location.current.temp;
        const tempFahrenheit = (tempCelsius * 1.8) + 32;
        setFahrenheit(tempFahrenheit); // Store Fahrenheit temperature
    }

    function saveToLocalStorage(data) {
        const weatherData = {
            location: data,
            timestamp: Date.now(),
        };
        localStorage.setItem('currentWeather', JSON.stringify(weatherData));
    }

    function loadFromLocalStorage() {
        const cachedData = localStorage.getItem('currentWeather');
        if (cachedData) {
            const { location, timestamp } = JSON.parse(cachedData);

            // Use the cached data if it's less than 6 hours old
            if (Date.now() - timestamp < 6 * 60 * 60 * 1000) {
                setLocation(location);
                calculate(location);
            } else {
                setError('Cached data is too old. Please connect to the internet to get the latest weather information.');
            }
        } else {
            setError('No cached data available. Please connect to the internet.');
        }
        setIsLoading(false);
    }

    function toggleTemperatureUnit() {
        setTemperatureUnit((prevUnit) => (prevUnit === 'C' ? 'F' : 'C'));
    }

    // Function to display browser notification
    function showNotification(title, message) {
        if ("Notification" in window && Notification.permission === "granted") {
            new Notification(title, { body: message });
        } else if (Notification.permission !== "denied") {
            Notification.requestPermission().then((permission) => {
                if (permission === "granted") {
                    new Notification(title, { body: message });
                }
            });
        }
    }

    // Determine the temperature to display based on the selected unit
    const displayTemperature = temperatureUnit === 'C' 
        ? location?.current.temp
        : fahrenheit;

    return (
        <div>
            {isLoading ? (
                <h1>Loading...</h1>
            ) : error ? (
                <h1>{error}</h1>
            ) : (
                location && (
                    <>
                        <h1 className='current'>Your Current Location: {location.timezone}</h1>
                        <ul className="Current">
                            <h3>Today's Weather</h3>
                            <img src={`https://openweathermap.org/img/wn/${location.current.weather[0].icon}.png`} alt={location.current.weather[0].description} />
                            <li>Location: {location.timezone}</li>
                            <li>Temperature: {displayTemperature}Â°{temperatureUnit}</li>
                            <li>Weather: {location.current.weather[0].description}</li>
                            <li>Humidity: {location.current.humidity}%</li>
                            <li>Wind Speed: {location.current.wind_speed} m/s</li>
                            <button 
                                className='change'
                                onClick={toggleTemperatureUnit}>
                                Switch to {temperatureUnit === 'C' ? 'Fahrenheit' : 'Celsius'}
                            </button>
                        </ul>

                        {alerts && alerts.length > 0 && (
                            <div className="alerts">
                                <h2>Weather Alerts</h2>
                                {alerts.map((alert, index) => (
                                    <div key={index} className="alert">
                                        <h3>{alert.event}</h3>
                                        <p>{alert.description}</p>
                                        <p>Effective: {new Date(alert.start * 1000).toLocaleString()}</p>
                                        <p>Expires: {new Date(alert.end * 1000).toLocaleString()}</p>
                                    </div>
                                ))}
                            </div>
                        )}
                    </>
                )
            )}
        </div>
    );
}
